    }
  });
};

var styles = StyleSheet.create({
  card: {
    backgroundColor: "white",
    padding: 10,
    margin: 1,
    borderWidth: 1,
    borderColor: "#dcdcdc",
    borderRadius: 5,
    textAlign: "center",
    justifyContent: "center"
  },
  card2: {
    backgroundColor: "white",
    padding: 10,
    borderWidth: 1,
    borderColor: "#dcdcdc",
    borderRadius: 5,
    textAlign: "center",
    justifyContent: "center"
  },
  adcard: {
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center"
  },
  textInput: {
    flex: 1,
    marginTop: Platform.OS === 'ios' ? 0 : -12,
    paddingLeft: 10,
    width: "90%",
    color: '#05375a'
  }
});

